function ajaxSearch() {
  var input_data = $('.search-query').val();
  if (input_data.length < 2) {
    $('.searchResults').html("");
  } else {
    $.ajax({
      type: "POST",
      url: "ajax/search",
      data: "keyword="+input_data,
      
      success: function(data) {
      // return success
        if (data.length > 0) {
          //$('#searchResults').show();
          $('.searchResults').html(data);
        } else {
          //$('#searchResults').hide();
        }
      }
    });
  }
}
$(document).ready(function(){
  $('.anime-item').each(function () {
	  $(this).tooltip({
		  html: true,
		  title: $(this).children(".anime-info").html(),
		  placement: 'auto right',
	  });
    /*$(this).tooltip(
    {
      html: true,
      title: $(this).data('tip')).html()
    });*/
  });
  bookmark = JSON.parse(localStorage.getItem("bookmark"));
  if(!bookmark) bookmark = new Array();
  if($(".bookmark-body")[0]){
    $.ajax({
      type: "POST",
      data: "data="+localStorage.getItem("bookmark"),
      url: "ajax/bookmark",
      success: function(string){
        console.log("OK");
        $(".bookmark-body").html(string);
          $(".bookmark-close-btn").click(function(){
            animeid = $(this).data("animeid");
            saved = JSON.parse(localStorage.getItem("bookmark"));
            save = jQuery.grep(saved, function(value) {
              return value != animeid;
            });
            localStorage.setItem("bookmark", JSON.stringify(save));
            $(this).parent().parent().remove();
            $(".bookmark-count").html(JSON.parse(localStorage.getItem("bookmark")).length);
          });
      },
    });
  }
  animeid = $(".bookmark-btn").data("animeid")
  $(".bookmark-count").html(bookmark.length);
  if($.inArray(animeid, bookmark) >= 0){
    $(".bookmark-btn").hide();
    $(".bookmark-remove-btn").show();
    //  $(".bookmark-btn").removeClass("btn-primary bookmark-btn").addClass("btn-default bookmark-remove-btn").html("Xóa xem sau");
  }
  $(".bookmark-btn").click(function(){
    animeid = $(this).data("animeid");
    save = new Array();
    if(localStorage.getItem("bookmark")){
      saved = localStorage.getItem("bookmark");
      save = JSON.parse(saved);
    }
    console.log("OK");
    console.log(save);
    save.unshift(animeid);
    localStorage.setItem("bookmark", JSON.stringify(save));
    $(".bookmark-btn").hide();
    $(".bookmark-remove-btn").show();
  });
  $(".bookmark-remove-btn").click(function(){
    animeid = $(this).data("animeid");
    saved = JSON.parse(localStorage.getItem("bookmark"));
    save = jQuery.grep(saved, function(value) {
      return value != animeid;
    });
    localStorage.setItem("bookmark", JSON.stringify(save));
    $(".bookmark-btn").show();
    $(".bookmark-remove-btn").hide();
  });
});

$("#report-btn").click(function(){
  var id = $('#episodeID').val();
  var content = $('#report-content').val();
  $.ajax({
    type: "POST",
    url: "ajax/report",
    dataType: "text",
    data: {
      'id': id,
      'content' : content
    },
    success: function(string){
      var result = $.parseJSON(string);
      if(result.success){
        $("#reportBox").html("Báo lỗi thành công với ID: "+result.id);
      }
    }
  })
});
